import { Fr } from '@aztec/foundation/fields';
import { z } from 'zod';
import { schemas } from '../schemas/index.js';
export function randomInBlock(data) {
    return {
        data,
        l2BlockNumber: Math.floor(Math.random() * 1000),
        l2BlockHash: Fr.random().toString()
    };
}
export async function wrapInBlock(data, block) {
    return {
        data,
        l2BlockNumber: block.number,
        l2BlockHash: (await block.hash()).toString()
    };
}
export function inBlockSchemaFor(schema) {
    return z.object({
        data: schema,
        l2BlockNumber: schemas.Integer,
        l2BlockHash: z.string()
    });
}
