export class ScopedValueCache {
    cache = new Map();
    constructor(items){
        items.forEach((item)=>{
            const value = item.value.toBigInt();
            const arr = this.cache.get(value) ?? [];
            arr.push(item);
            this.cache.set(value, arr);
        });
    }
    get(matcher) {
        return this.cache.get(matcher.value.toBigInt()) ?? [];
    }
}
